import React, { useState } from 'react';

import classes from './addressForm.module.css';

function AddressForm(props){
    const [mob, setMob] = useState(" ");
    const [pin, setPin] = useState(" ");

    function addressSave(event){  
        event.preventDefault();

        //Mobile no validation

        if(mob.length < 10)
            alert("Minimum length is 10");
        else if(/^\d+$/.test(mob) === false)
            alert("Please enter a valid 10 digit mobile number");
        else if(mob.charAt(0) < 6)
            alert("Please enter a valid 10 digit mobile number");

        //Pin code validation

        if(pin.length < 6)
            alert("Minimum length is 10");
        else if(/^\d+$/.test(pin) === false)
            alert("Please enter a valid 10 digit mobile number");
    }
    function mobHandler(event){
        const no = event.target.value;
        setMob(no);    
    }
    function pinHandler(event){
        const no = event.target.value;
        setPin(no);    
    }


    return(
        <form onSubmit={addressSave}>
            <div className={classes.con}>
                <p className={classes.head}>CONTACT DETAILS</p>
                <div className={classes.details}>
                    <input 
                        type= "text" 
                        placeholder=' ' 
                        required 
                        className={classes.textBox}>
                    </input>
                    <label className={classes.formLabel}>
                        Name*
                    </label>
                </div>
                <div className={classes.details}>
                    <input 
                        type= "text" 
                        placeholder=' ' 
                        maxlength="10" 
                        required 
                        className={classes.textBox}
                        onChange = {mobHandler}>
                    </input>
                    <label className={classes.formLabel}>
                        Mobile No*
                    </label>
                </div>
            </div>
            <div className={classes.add}>
                <p className={classes.head}>ADDRESS</p>
                <div className={classes.details}>
                    <input 
                        type= "text" 
                        placeholder=' ' 
                        maxlength="6" 
                        required 
                        className={classes.textBox}
                        onChange = {pinHandler}>
                    </input>
                    <label className={classes.formLabel}>
                        Pin Code*
                    </label>
                </div>
                <div className={classes.details}>
                    <input 
                        type= "text" 
                        placeholder=' '  
                        required 
                        className={classes.textBox}>
                    </input>
                    <label className={classes.formLabel}>
                        Address(House No, Building, Street, Area)*
                    </label>
                </div>
                <div className={classes.details}>
                    <input 
                        type= "text" 
                        placeholder=' '  
                        required 
                        className={classes.textBox}>
                    </input>
                    <label className={classes.formLabel}>
                        Locality/Town*
                    </label>
                </div>
            </div>
            <button type= "submit" value="Submit" className={classes.save}>ADD ADDRESS</button>
        </form>
    );
}

export default AddressForm;
