import { useEffect } from 'react';
import { useSelector } from 'react-redux';

function CartData(cart, bill){
    const userSub = useSelector((state) => state.login.userData);

    const newEmail = userSub.email;
    const newCart = cart;
    const newBill = bill;

    useEffect(() =>{
        async function postData(){
    
            const res = await fetch("/api/user/history-create", {
                method: "POST",
                headers:{
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ email: newEmail, cart: newCart, bill: newBill })
            });
        
            await res.json();
    
        }

        postData();

    }, [newEmail, newCart, newBill]);
    
   
}

export { CartData };
