import { createSlice } from "@reduxjs/toolkit";

const initialCartState = {items: [], bill: [{ tmrp: 0, tdis: 0, tdelfee: 0, amount: 0}]};

const cartSlice = createSlice({
    name: 'cartItems',
    initialState: initialCartState,
    reducers: {
        addItem(state, action){
            const newItem = action.payload;
            const existItem = state.items.find(item => item.id === newItem.id && item.sz === newItem.sz);

            if(existItem && existItem.quantity === 10){
                return;
            }
            if(!existItem){
                state.items.push({
                    id: newItem.id,
                    image: newItem.image,
                    company: newItem.company,
                    product: newItem.product,
                    rating: newItem.rating,
                    sp: newItem.sp,
                    mrp: newItem.mrp,
                    discount: newItem.discount,
                    quantity: 1,
                    sz: newItem.sz,
                    delfee: newItem.delfee,
                });
            }
            else{
                existItem.quantity++;
                existItem.mrp = existItem.mrp + newItem.mrp;
                existItem.sp = existItem.sp + newItem.sp;
            }

            state.bill.tmrp = state.bill.tmrp + newItem.mrp;
            state.bill.tdis = state.bill.tdis + newItem.discount;
            state.bill.tdelfee = state.bill.tdelfee + newItem.delfee;
            state.bill.amount = state.bill.tmrp + state.bill.tdelfee - state.bill.tdis;
        },
        removeItem(state, action){
            const delItem = action.payload;
            const existItem = state.items.find(item => item.id === delItem.id && item.sz === delItem.sz);

            state.bill.tmrp = state.bill.tmrp - existItem.mrp;
            state.bill.tdis = state.bill.tdis - existItem.discount;
            state.bill.tdelfee = state.bill.tdelfee - existItem.delfee;
            state.bill.amount = state.bill.tmrp + state.bill.tdelfee - state.bill.tdis;


            state.items = state.items.filter(item => item.id !== delItem.id || item.sz !== delItem.sz);
        }
    }
});

export const cartActions = cartSlice.actions;

export default cartSlice.reducer;
