import { createSlice } from "@reduxjs/toolkit";

const initialLoginState = {
    token: '', 
    loggedin: false
};

const loginSlice = createSlice({
    name: 'login',
    initialState: initialLoginState,
    reducers: {

    },
});

export const loginActions = loginSlice.actions;

export default loginSlice.reducer;
