import { createSlice } from "@reduxjs/toolkit";

const initialAddressState = {
    name: "",
    mob: "",
    house: "",
    town: "",
    landmark: "",
    city: "",
    state: "",
    pin: ""
};

const addressSlice = createSlice({
    name: 'address',
    initialState: initialAddressState,
    reducers: {
        addAddress(state, action){
            const address = action.payload;
            state.name = address.name;
            state.mob = address.mobile;
            state.house = address.house;
            state.town = address.town;
            state.landmark = address.landmark;
            state.city = address.city;
            state.state = address.state;
            state.pin = address.pin;
            console.log(state.name +" "+ state.mob+" "+ state.pin +" "+ state.house +" "+ state.town+" "+ state.landmark +" "+ state.city +" "+ state.state);
        }
    },
});



export const addressActions = addressSlice.actions;

export default addressSlice.reducer;
