import { useEffect } from 'react';
import { useSelector } from 'react-redux';

function CartData(){
    //const userSub = useSelector((state) => state.login.userData);

    const email = "user@example.com";
    const cart = localStorage.getItem("products");
    const bill = localStorage.getItem("billStore");

    useEffect(() =>{
        async function postData(){
    
            const res = await fetch("/api/user/history-create", {
                method: "POST",
                headers:{
                    "Content-Type": "application/json"
                },
                body: JSON.stringify({ email, cart, bill })
            });
        
            await res.json();
    
        }

        postData();

    }, [email, cart, bill]);
    
   
}

export { CartData };
