import React, { useState } from 'react';
import classes from './shoeMenu.module.css';

function ShoeMenu(props) {
    const [dropDown, setDropDown] = useState(false);
    function dropDownHandler(event){
        if(dropDown === true)
            setDropDown(false);
        else
            setDropDown(true);
        return;
    }
    return(
        <div className={classes.shoeAccordian}>
            <div className={classes.menu}>
                <ul>
                    <li onmouseover={dropDownHandler}>Men</li>
                    <li>Women</li>
                    <li>Kids</li>
                </ul>
            </div>
            <div className={`${dropDown && classes.dropTypes}  ${!dropDown && classes.types}`}>hello</div>
        </div>
    );
}

export default ShoeMenu;
