import React, { useState } from 'react';

import BadgeCheckIcon from '../../icons/badgeCheckIcon';
import classes from './paymentOptions.module.css';

function PaymentOptions(props){
    const bill = localStorage.getItem("billStore") ? JSON.parse(localStorage.getItem("billStore")) : 0;
    const [ops, setOps] = useState(" ");

    function pay(event){

        if(ops === " ")
            return;
    }

    return(
        <div className={classes.paymentCard}>
            <div className={classes.summary}>

                Order Total &#8377; {bill[0].amount}
            </div>
            PAYMENT OPTIONS
            <ul className={classes.paymentOps}>
                <li onClick = {() => setOps("cash")} className = {`${ops === "cash" && classes.activeOps}  ${ops !== "cash" && ''}`}>
                    Cash On Delivery
                </li>
                <li onClick = {() => setOps("online")} className = {`${ops === "online" && classes.activeOps}  ${ops !== "online" && ''}`}>
                <span className={classes.check}><BadgeCheckIcon /></span> Pay Now
                    <div className={classes.subHead}>Debit Card, Credit Card, Net Banking, UPI</div>
                </li>
                <li><button onClick = {pay} className={classes.continue}>CONTINUE</button></li>
            </ul>
        </div>
    );
}

export default PaymentOptions;
