import React, { useState } from 'react';
import classes from './shoeMenu.module.css';

function ShoeMenu(props) {
    const [addWish, setAddWish] = useState(false);
    function wishHandler(event){
        if(addWish === true)
            setAddWish(false);
        else
            setAddWish(true);
        return;
    }

    return(
        <div>
            <div className={classes.menu}>
                <ul>
                    <li onMouseHover = {wishHandler}>Men</li>
                    <li>Women</li>
                    <li>Kids</li>
                </ul>
            </div>
            <div className = {`${classes.types} ${addWish && classes.activeTypes}  ${!addWish && ''}`}>hello</div>
        </div>
    );
}

export default ShoeMenu;
